import React from 'react';
import styled from 'styled-components';
import DownButton from '../ui/Button';
import { useLocation } from 'react-router-dom'; // useLocation Ï∂îÍ∞Ä
import { useNavigate } from 'react-router-dom';
import { createGlobalStyle } from 'styled-components';
import PDFPreview from '../ui/PDFPreview';

const GlobalStyle = createGlobalStyle`
  @font-face {
    font-family: 'NanumNeo';
    src: url('/fonts/NanumSquareR.ttf') format('truetype');
  }
    body {
    font-family: 'NanumNeo', sans-serif;
    }
 `

const Wrapper = styled.div`
  padding: 30px;
  display: flex;
  flex-direction: column;
  height: auto;
  background: linear-gradient(to bottom, #83C4FF, #FFFFFF);
  padding-top: 90px;
`;

const Box = styled.div`
    padding-bottom: 30px;
    background-color: white;
    border-radius: 15px;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    min-height: 270px;
    max-height: 270px;
    margin-bottom: 20px;
    box-shadow: 0px 0px 8px rgba(0, 0, 0, 0.1);
`;

const MainTitleText = styled.p`
    font-size: 25px;
    font-weight: bold;
    text-align: center;
    margin-bottom: -5px;
`;

const SubTitleText = styled.p`
    font-size: 11px;
    text-align: center;
    padding-bottom: 40px;
`;

const SecondPage = () => {
  const location = useLocation(); // useLocation ÌõÖ ÏÇ¨Ïö©
  const navigate = useNavigate();

  const handleButtonClick = () => {
    console.log("Button clicked!");
    /* Îã§Ïö¥Î°úÎìú Í∏∞Îä• */
    const pdfUrl = location.state && location.state.pdfUrl;

    /* html 5Ïùò 'a'ÌÉúÍ∑∏Î•º ÌôúÏö©ÌïòÏó¨ link ÏÉùÏÑ±*/
    if (pdfUrl) {
      const fileName = pdfUrl.substring(pdfUrl.lastIndexOf('/') + 1); // URLÏóêÏÑú ÌååÏùº Ïù¥Î¶Ñ Ï∂îÏ∂ú
      const link = document.createElement('a');
      link.href = pdfUrl;
      link.setAttribute('download', fileName); // Ï∂îÏ∂úÌïú ÌååÏùº Ïù¥Î¶ÑÏúºÎ°ú Îã§Ïö¥Î°úÎìú
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    }

  };

  // First-pageÏóêÏÑú Ï†ÑÎã¨Îêú URL Í∞ÄÏ†∏Ïò§Í∏∞
  const pdfUrl = location.state && location.state.pdfUrl;

  return (
    <div>
      <GlobalStyle />
      <Wrapper>
        <MainTitleText>HANDCHIVE</MainTitleText>
        <SubTitleText>ÎÇ¥ ÏÜêÏúºÎ°ú ÏßÅÏ†ë Í∑∏Î¶¨Îäî Î¨∏ÏÑú</SubTitleText>
        <Box>
          {/* Ï†ÑÎã¨Î∞õÏùÄ PDF URLÎ°ú PDFÎ•º Î†åÎçîÎßÅ */}
          {pdfUrl && <PDFPreview pdfUrl={pdfUrl} />}
        </Box>
        <Button
          title="Download üì•"
          onClick={handleButtonClick}
        />
      </Wrapper>
    </div>
  );
};

export default SecondPage;
