const Wrapper = styled.div`
  padding: 40px;
  display: flex;
  flex-direction: column;
  height: 100vh;
  background: linear-gradient(to bottom, #83C4FF, #FFFFFF); /* Í∑∏ÎùºÎç∞Ïù¥ÏÖò ÏÉâÏÉÅ Ï°∞Ï†ï */
