    box-shadow: 20px;
    border-radius: 15px;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    height: 350px;
`;


// Ï†úÎ™© Îã¥Îãπ
const MainTitleText = styled.p`
    font-size: 37px;
    font-weight: bold;
    text-align: center;
    margin-bottom: -10px;
`;

const SubTitleText = styled.p`
    font-size: 15px;
    text-align: center;
    padding-bottom: 40px;
`;

// ÏóÖÎ°úÎìú Ïª¥Ìè¨ÎÑåÌä∏ Ï†ïÎ†¨
const UploadContainer = styled.div`
    margin-top: 40px;
    display: flex;
    justify-content: center;
    align-items: center;
`;

const ButtonContainer = styled.div`
  display: flex;
  justify-content: flex-end;
`;

const FirstPage = () => {
    const navigate = useNavigate();

    const handleFileSelect = (file) => {
    // ÏÑ†ÌÉùÌïú ÌååÏùºÏùÑ Ï≤òÎ¶¨ÌïòÎäî Î°úÏßÅÏùÑ Ï∂îÍ∞ÄÌï† Ïàò ÏûàÏäµÎãàÎã§.
    console.log('Selected file:', file);
