  };

  const handleButtonClick = () => {
    console.log("Button clicked!");
    navigate('second-page'); //ÌéòÏù¥ÏßÄ Ïù¥ÎèôÏãú ÏÇ¨Ïö©
  };

  return (
  <div>
    <Wrapper>
        <MainTitleText>üìù HandChive</MainTitleText>
        <SubTitleText>ÎÇ¥ ÏÜêÏúºÎ°ú ÏßÅÏ†ë Í∑∏Î¶¨Îäî Î¨∏ÏÑú</SubTitleText>
        <Box>
            <UploadContainer>
                <ImgUpload onFileSelect={handleFileSelect} />
            </UploadContainer>
            <ButtonContainer>
                <Button
                    title = "Î≥ÄÌôòÌïòÍ∏∞"
                    onClick={handleButtonClick} />
            </ButtonContainer>
        </Box>
    </Wrapper>
  </div>
  );
};

export default FirstPage;
